mapper.update:
UPDATE CAFE
NAME = #{name}, PHONE_NUMBER= #{phoneNumber}, ROAD_ADDRESS = #{roadAddress}, DETAIL_ADDRESS = #{detailAddress}, SI_GUN_GU_ID = #{siGunGuId}, LATITUDE = #{latitude}, LONGITUDE = #{longitude}, START_TIME = #{startTime}, END_TIME = #{endTime}, DESCRIPTION = #{description}, IMAGE_PATH = #{imagePath}, BUSINESS_NUMBER = #{businessNumber},
WHERE ID = #{id}

mapper.select:
SELECT "id", "cafeName", "siDoName", "siGunGuName", "roadAddress", "detailAddress", "latitude", "longitude", "startTime", "endTime", "imagePath", "cafeRating", "minPrice", "reviewCount", "resCount" FROM (SELECT CEIL(ROWNUM / #{elementSize}) "request_page", "id", "cafeName", "siDoName", "siGunGuName", "roadAddress", "detailAddress", "latitude", "longitude", "startTime", "endTime", "imagePath", "cafeRating", "minPrice", "reviewCount", "resCount" FROM (SELECT c.ID "id", c.NAME "cafeName", do.NAME "siDoName", si.NAME "siGunGuName", ROAD_ADDRESS "roadAddress", DETAIL_ADDRESS "detailAddress", LATITUDE "latitude", LONGITUDE "longitude", START_TIME "startTime", END_TIME "endTime", IMAGE_PATH "imagePath", "cafeRating", "minPrice", "reviewCount", "resCount" FROM CAFE c LEFT JOIN (SELECT res.CAFE_ID, AVG(rev.RATING) "cafeRating" FROM REVIEW rev JOIN RESERVATION res ON rev.RESERVATION_ID = res.ID JOIN member mem ON rev.MEMBER_ID = mem.ID WHERE TYPE = 'guest' GROUP BY res.CAFE_ID) a ON a.CAFE_ID = c.ID LEFT JOIN (SELECT CAFE_ID, MIN(PRICE) "minPrice", MAX(HEAD_COUNT) "maxPerson" FROM ROOM roo JOIN CAFE ON CAFE_ID = CAFE.ID GROUP BY CAFE_ID) b ON b.CAFE_ID = c.ID LEFT JOIN SI_GUN_GU si ON si.ID = c.SI_GUN_GU_ID LEFT JOIN SI_DO do ON do.ID = si.SI_DO_ID LEFT JOIN (SELECT res.CAFE_ID, COUNT(res.ID) "reviewCount" FROM RESERVATION res JOIN REVIEW rv ON res.ID = rv.RESERVATION_ID JOIN MEMBER mem ON rv.MEMBER_ID = mem.ID WHERE TYPE = 'guest' GROUP BY res.CAFE_ID) c ON c.CAFE_ID = c.ID LEFT JOIN (SELECT res.CAFE_ID, COUNT(res.ID) "resCount" FROM RESERVATION res WHERE res.STATUS = 'use' OR res.STATUS = 'complete' GROUP BY res.CAFE_ID) d ON d.CAFE_ID = c.ID
STATUS = 'enabled' AND c.NAME LIKE '%' || #{cafeSearch.name} || '%'
AND c.ID IN (SELECT ca.ID FROM CAFE ca JOIN CAFE_OPTION_LIST col ON col.CAFE_ID = ca.ID WHERE col.OPTION_ID = #{cafeSearch.option})
AND "maxPerson" >= #{cafeSearch.countPerson}
ORDER BY REGDATE DESC
, DISTNACE_WGS84(LATITUDE, LONGITUDE, #{cafeSearch.currentY}, #{cafeSearch.currentX}) ASC )) WHERE "request_page" = #{requestPage}

SELECT "id", "cafeName", "siDoName", "siGunGuName", "roadAddress", "detailAddress", "latitude", "longitude", "startTime", "endTime", "imagePath", "cafeRating", "minPrice" FROM (SELECT CEIL(ROWNUM / #{pageParams.elementSize}) "request_page", "id", "cafeName", "siDoName", "siGunGuName", "roadAddress", "detailAddress", "latitude", "longitude", "startTime", "endTime", "imagePath", "cafeRating", "minPrice" FROM (SELECT c.ID "id", c.NAME "cafeName", do.NAME "siDoName", si.NAME "siGunGuName", ROAD_ADDRESS "roadAddress", DETAIL_ADDRESS "detailAddress", LATITUDE "latitude", LONGITUDE "longitude", START_TIME "startTime", END_TIME "endTime", IMAGE_PATH "imagePath", "cafeRating", "minPrice" FROM CAFE c LEFT JOIN (SELECT res.CAFE_ID, AVG(rev.RATING) "cafeRating" FROM REVIEW rev JOIN RESERVATION res ON rev.RESERVATION_ID = res.ID JOIN member mem ON rev.MEMBER_ID = mem.ID WHERE TYPE = 'guest' GROUP BY res.CAFE_ID) a ON a.CAFE_ID = c.ID LEFT JOIN (SELECT CAFE_ID, MIN(PRICE) "minPrice", MAX(HEAD_COUNT) "maxPerson" FROM ROOM roo JOIN CAFE ON CAFE_ID = CAFE.ID GROUP BY CAFE_ID) b ON b.CAFE_ID = c.ID LEFT JOIN SI_GUN_GU si ON si.ID = c.SI_GUN_GU_ID LEFT JOIN SI_DO do ON do.ID = si.SI_DO_ID LEFT JOIN (SELECT res.CAFE_ID, COUNT(res.id) "reviewCount" FROM RESERVATION res JOIN review rv ON res.ID = rv.reservation_id JOIN MEMBER mem ON rv.MEMBER_ID = mem.ID WHERE TYPE = 'guest' GROUP BY res.CAFE_ID) c ON c.CAFE_ID = c.ID LEFT JOIN (SELECT res.CAFE_ID, COUNT(res.ID) "resCount" FROM RESERVATION res WHERE res.STATUS = 'use' OR res.STATUS = 'complete' GROUP BY res.CAFE_ID) d ON d.CAFE_ID = c.ID
STATUS = 'enabled' AND c.NAME LIKE '%' || #{cafeSearch.name} || '%'
AND DISTNACE_WGS84(LATITUDE, LONGITUDE, #{cafeSearch.currentY}, #{cafeSearch.currentX}) >= #{cafeSearch.maxDuration}
AND c.ID IN (select ca.id from CAFE ca join CAFE_OPTION_LIST col ON col.CAFE_ID = ca.ID where col.OPTION_ID = #{cafeSearch.searchOption})
AND "maxPerson" >= #{cafeSearch.countPerson}
ORDER BY DISTNACE_WGS84(LATITUDE, LONGITUDE, #{cafeSearch.currentY}, #{cafeSearch.currentX}) ASC )) WHERE "request_page" = #{pageParams.requestPage}

mapper.update:
UPDATE CAFE
SET STATUS = 'disabled'
WHERE ID = #{id}
