mapper.select:
SELECT ID
FROM GROUP_CONTACT
WHERE MEMBER_ID = #{id} AND STUDY_GROUP_ID = #{studyGroupId}

mapper.update:
UPDATE GROUP_CONTACT
SET STATUS = 'approve'
WHERE MEMBER_ID = #{id} AND STUDY_GROUP_ID = #{studyGroupId}

UPDATE GROUP_CONTACT
SET STATUS = 'refuse'
WHERE MEMBER_ID = #{id} AND STUDY_GROUP_ID = #{studyGroupId}
