mapper.select:
SELECT ID, NAME, HEAD_COUNT, REGDATE, IMAGE_PATH, SUBJECT, STATUS, SI_GUN_GU_ID
FROM STUDY_GROUP
WHERE ID = #{studyGroupId}

mapper.update:
UPDATE STUDY_GROUP
NAME = #{name}, HEAD_COUNT = #{headCount}, IMAGE_PATH = #{imagePath}, SUBJECT = #{subject},
WHERE ID = #{id}

UPDATE STUDY_GROUP
SET status = 'disabled'
WHERE ID = #{id}
