mapper.update:
UPDATE STUDY_GROUP
NAME = #{name}, TOTAL_COUNT = #{totalCount}, IMAGE_PATH = #{imagePath}, SUBJECT = #{subject},
WHERE ID = #{id}

UPDATE STUDY_GROUP
SET status = 'disabled'
WHERE ID = #{id}

UPDATE STUDY_GROUP
SET CURRENT_COUNT = CURRENT_COUNT + 1
WHERE ID = #{id}

UPDATE STUDY_GROUP
SET CURRENT_COUNT = CURRENT_COUNT - 1
WHERE ID = #{id}
