mapper.select:
SELECT ID, EMAIL, NAME, PHONE_NUMBER, BIRTHDAY, GENDER, TYPE, TO_CHAR(regdate, 'yyyy-MM-DD HH24:MI:SS') regdate, IMAGE_PATH
FROM MEMBER
WHERE id = #{id}

mapper.update:
UPDATE MEMBER
NAME = #{name}, PASSWORD = #{password}, PHONE_NUMBER = #{phoneNumber}, BIRTHDAY = #{birthday}, GENDER = #{gender}, IMAGE_PATH = #{imagePath},
WHERE ID = #{id}

mapper.select:
SELECT NAME, PHONE_NUMBER, BIRTHDAY, GENDER, IMAGE_PATH
FROM MEMBER
WHERE id = #{id}

mapper.update:
UPDATE MEMBER
SET status = 'disabled'
WHERE ID = #{id}
