mapper.update:
UPDATE ROOM
NAME = #{name}, DESCRIPTION = #{description}, IMAGE_PATH = #{imagePath}, HEAD_COUNT = #{headCount}, PRICE = #{price},
WHERE ID = #{id}

mapper.select:
SELECT ID, NAME, DESCRIPTION, IMAGE_PATH, HEAD_COUNT, PRICE, STATUS, CAFE_ID
FROM Room
WHERE ID = #{id}

mapper.update:
UPDATE Room
SET status = 'disabled'
WHERE ID = #{id}
