mapper.select:
SELECT COUNT(ID) "count"
FROM CONTACT

    AND STATUS = #{type}

SELECT ID, EMAIL, NAME, CAFE_NAME, PHONE_NUMBER, ADDRESS, MESSAGE, BUSINESS_NUMBER, TO_CHAR(CONTACT_DATE, 'yyyy-MM-dd') "contact_date", STATUS FROM (SELECT CEIL(ROWNUM / #{elementSize}) "request_page", ID, EMAIL, NAME, CAFE_NAME, PHONE_NUMBER, ADDRESS, MESSAGE, BUSINESS_NUMBER, CONTACT_DATE, STATUS FROM (SELECT ID, EMAIL, NAME, CAFE_NAME, PHONE_NUMBER, ADDRESS, MESSAGE, BUSINESS_NUMBER, CONTACT_DATE, STATUS FROM CONTACT
STATUS = #{type}
ORDER BY CONTACT_DATE)) WHERE "request_page" = #{requestPage}
