mapper.select:
SELECT "id", "title", "content", "category" FROM (SELECT CEIL(ROWNUM / #{elementSize}) "request_page", "id", "title", "content", "category" FROM (SELECT f.ID "id", TITLE "title", CONTENT "content", c.NAME "category" FROM FAQ f JOIN FAQ_CATEGORY c ON f.CATEGORY_ID = c.ID
c.NAME = #{type}
ORDER BY CATEGORY_ID)) WHERE "request_page" = #{requestPage}

SELECT ID, TITLE, CONTENT, CATEGORY_ID
FROM FAQ
WHERE CATEGORY_ID = #{categoryId}

mapper.update:
UPDATE FAQ
TITLE = #{title}, CONTENT = #{content}, CATEGORY_ID = #{categoryId},
WHERE ID = #{id}
